class Admin_Model_Category extends Admin_Model_Base_Category
{
	public static function getCategory(){
		$model = __CLASS__ ;
		$query		= Doctrine_Query::create()
						->setHydrationMode(Doctrine_Core::HYDRATE_ARRAY) 
						->from("$model t INDEXBY t.id");
		$data =  $query->execute();
			
		if(empty($data)){
			$data = self::getDefaultTableDetails($model);			 
		}

		return $data;
	}
}